import { useContext, useEffect, useState } from "react";
import { AuthenContext } from "../context/AuthenContext";
import { API_URL } from "../../configapi";
import axios from "axios";

const ListDeThiTHPTQG=(props)=>{
    const list=useContext(AuthenContext)
    const {diem}=useContext(AuthenContext)
    console.log(diem);
    const [data,setData]=useState([])
    const [current,setCurrent]=useState(0)
    useEffect(()=>{
        axios.get(API_URL)
        .then(res=>{
            setData(res.data)
        })
        .catch(err=>{
            console.log(err);
        })
    },[])

    return(
        <div className="thptqg">
            <h1>Chào mừng {list.username} đến với bài kiểm tra</h1>
            <Box data={data} current={current} handler={setCurrent}/>
        </div>
    )
}
const Box=({data,current,handler})=>{
    return(
        <div className="thi">
            {
                data.map((items)=>{
                    return(
                        <div className="items">
                            <div className="">{items.cauhoi}</div>
                        </div>
                    )
                })
            }
        </div>
    )
}
export default ListDeThiTHPTQG;
