    // useEffect(() => {
    //     const timer = setTimeout(() => {
    //         setTimeLeft(demthoigian());
    //     }, 1000)
    // });
    // const timerComponents = []
    // Object.keys(timeLeft).forEach((interval) => {
    //     if (!timeLeft[interval]) {
    //         return;
    //     }

    //     timerComponents.push(
    //         <span>
    //             {timeLeft[interval]} {interval}{" "}
    //         </span>
    //     );
    // });
    return (
        <div className="thptqg">
            <h1>Bài kiểm tra môn Toán</h1>
            <h1>Lớp</h1>
            <p>Thời gian làm bài {diem}</p>
            <p>Thời gian làm bài còn lại</p>
            {/* {timerComponents.length ? timerComponents : <span>Time's up!</span>} */}
            <h1>Chào mừng {list.username} đến với bài kiểm tra</h1>
            <h1>{ }</h1>
            <Box current={current} next={setCurrent} />
        </div>
    )
}
const Box = ({ current, next }) => {
    const { data, diem, setDiem } = useContext(AuthenContext)
    console.log(data);
    const {exit,start,setExit,setStart}=useContext(AuthenContext)
    console.log(exit,start);
    const [api,setApi]=useState([])
    useEffect(()=>{
        axios.get(`${process.process.env.REACT_APP_API_TEST}`)
        .then(res=>{
            console.log(res.data);
            setApi(res.data)
        })
        .catch(err=>{
            console.log(err);
        })
    })

    const tests = useContext(AuthenContext)
    const optradios={
        optradio:""
    }
    const [radios,setOptradio]=useState(optradios)
    const [ans, setAns] = useState("")
    const HandlerGtri=(e)=>{
        const {name,value}=e.target
        setOptradio({...radios,[name]:value})
    }
    const saveHandle = () => {
        if (data[current].kq === ans) {
            setDiem(diem + 1)
        }
        setAns("")
        setOptradio("")
        if (current + 1 === data.length) {
            setExit(true)
            
        } else {
            next(current + 1)
        }



    }
    const {optradio}=radios
    console.log(optradio);
    return (
        <div className="thi">
            {/* <div className="">{current + 1}{data[current].cauhoi}</div>
            <div className="col-sm-12">
                <div className="row">
                    <div className="col-sm-6 ">
                        <div className={`${tests.theme} ${ans === "A" ? tests.theme : tests.traloi} `} onClick={() => { setAns("A") }}>
                            A.{data[current].c1}
                        </div>
                        <div className={`${tests.theme} ${ans === "B" ? tests.theme : tests.traloi}`} onClick={() => { setAns("B") }}>
                            B.{data[current].c2}
                        </div>
                    </div>
                    <div className="col-sm-6">
                        <div className={`${tests.theme} ${ans === "C" ? tests.theme : tests.traloi}`} onClick={() => { setAns("C") }}>
                            C.{data[current].c3}
                        </div>
                        <div className={`${tests.theme} ${ans === "D" ? tests.theme : tests.traloi}`} onClick={() => { setAns("D") }}>
                            D.{data[current].c4}
                        </div>
                    </div>

                </div>
            </div> */}
            <div className="">
                {/* {
                    data.length >0 && (
                        <div className="">
                            <div className="">{current+1}{data[current].cauhoi}</div>
                            <div className="form-check">
                                <label className="form-check-label">
                                    <input type="radio"  onClick={()=>setAns("A")} onChange={HandlerGtri} value={data[current].kq}className="form-check-input" name="optradio" />{data[current].c1}
                                </label>
                            </div>
                            <div className="form-check">
                                <label className="form-check-label">
                                    <input type="radio" onClick={()=>setAns("B")} onChange={HandlerGtri} value={optradio}  className="form-check-input" name="optradio" />{data[current].c2}
                                </label>
                            </div>
                            <div className="form-check">
                                <label className="form-check-label">
                                    <input type="radio"  onClick={()=>setAns("C")} onChange={HandlerGtri} value={optradio}  className="form-check-input" name="optradio" />{data[current].c3}
                                </label>
                            </div>
                            <div className="form-check">
                                <label className="form-check-label">
                                    <input type="radio"  onClick={()=>setAns("D")} onChange={HandlerGtri} value={optradio} className="form-check-input" name="optradio" />{data[current].c4}
                                </label>
                            </div>
                        </div>
                    )
                } */}
                {
                    api.map((list)=>{
                        return(
                            <div className="tes">
                                <div>{list.cauhoi}</div> <br />
                                <div>{list.c1}</div>
                                <div>{list.c2}</div>
                                <div>{list.c3}</div>
                                <div>{list.c4}</div>
                            </div>
                        )
                    })
                }
            </div>
            <div className="col-sm-12 btn">
                <div className="row">
                    <div className="" style={{ width: "50%" }}>
                        <input type="button" value="Thoát" />
                    </div>
                    <div className="" style={{ width: "50%" }}>
                        <input type="button" onClick={saveHandle} value="Tiếp Theo" />
                    </div>
                </div>
            </div>
        </div>
    )
}
export default ListDeThiTHPTQG;
