import { useContext, useEffect, useState } from "react";
import { AuthenContext } from "../context/AuthenContext";
import "../layout/DeThi.css"
const ListDeThiTHPTQG = (props) => {
    const list = useContext(AuthenContext)
    const { diem } = useContext(AuthenContext)
    console.log(diem);
    const [current, setCurrent] = useState(0)
    return (
        <div className="thptqg">
            <h1>Bài kiểm tra môn Toán</h1>
            <h1>Lớp</h1>
            <p>Thời gian làm bài</p>
            <p>Thời gian làm bài còn lại</p>
            <h1>Chào mừng {list.username} đến với bài kiểm tra</h1>
            <Box current={current} />
        </div>
    )
}
const Box = ({ current }) => {
    const { data } = useContext(AuthenContext)
    console.log(data[current].cauhoi);
    const tests=useContext(AuthenContext)
    console.log(tests.theme);
    const [ans,setAns]=useState("")
    return (
        <div className="thi">
            <div className="">{data[current].cauhoi}</div>
            <div className="col-sm-12">
                <div className="row">
                    <div className="col-sm-6 ">
                        <div className={`${theme} ${ans==="A" }`}>
                            A.{data[current].c1}
                        </div>
                        <div className={theme}>
                            B.{data[current].c2}
                        </div>
                    </div>
                    <div className="col-sm-6">
                        <div className={theme}>
                            C.{data[current].c3}
                        </div>
                        <div className={theme}>
                            D.{data[current].c4}
                        </div>
                    </div>

                </div>
            </div>
            <div className="col-sm-12 btn">
                <div className="row">
                    <div className="" style={{ width: "50%" }}>
                        <input type="button" value="Thoát" />
                    </div>
                    <div className="" style={{ width: "50%" }}>
                        <input type="button" value="Tiếp Theo" />
                    </div>
                </div>
            </div>
        </div>
    )
}
export default ListDeThiTHPTQG;
